CREATE DATABASE Cosmetic;

USE Cosmetic;

CREATE TABLE User (
    id INT AUTO_INCREMENT PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    password VARCHAR(255) NOT NULL,
    name VARCHAR(40) NOT NULL,
    phone_number VARCHAR(12) UNIQUE NOT NULL,
    address VARCHAR(255),
    is_active BOOLEAN DEFAULT TRUE
);

CREATE TABLE SaleEvent (
    id INT AUTO_INCREMENT PRIMARY KEY,
    event_name VARCHAR(100),
    start_date DATE,
    end_date DATE,
    discount_percentage INT,
    is_active BOOLEAN DEFAULT FALSE
);

CREATE TABLE Item (
    id INT PRIMARY KEY,
    image_urls TEXT, -- Storing image URLs as a comma-separated string
    name VARCHAR(500),
    price DECIMAL(10, 3),
    brand VARCHAR(100),
    category VARCHAR(100),
    ingredients LONGTEXT,
    quantity INT,
    product_information LONGTEXT,
    use_information LONGTEXT,
    specifications LONGTEXT,
    is_on_sale BOOLEAN DEFAULT FALSE,
    sale_event_id INT,
    userRating DECIMAL(3, 2),
    rateCount INT,
    soldCount INT,
    FOREIGN KEY (sale_event_id) REFERENCES SaleEvent(id)
);


CREATE TABLE Cart (
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id INT,
    item_id INT,
    quantity INT,
    FOREIGN KEY (user_id) REFERENCES User(id),
    FOREIGN KEY (item_id) REFERENCES Item(id)
);

CREATE TABLE `Order` (
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id INT,
    order_date DATETIME,
    total_amount DECIMAL(10, 3),
    FOREIGN KEY (user_id) REFERENCES User(id)
);

CREATE TABLE OrderItem (
    id INT AUTO_INCREMENT PRIMARY KEY,
    order_id INT,
    item_id INT,
    quantity INT,
    FOREIGN KEY (order_id) REFERENCES `Order`(id),
    FOREIGN KEY (item_id) REFERENCES Item(id)
);

CREATE TABLE Receipt (
    id INT AUTO_INCREMENT PRIMARY KEY,
    order_id INT,
    payment_method VARCHAR(50),
    FOREIGN KEY (order_id) REFERENCES `Order`(id)
);

CREATE TABLE Admin (
    id INT AUTO_INCREMENT PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    password VARCHAR(255) NOT NULL
);

CREATE TABLE ProfitStatistics (
    id INT AUTO_INCREMENT PRIMARY KEY,
    item_id INT,
    sale_date DATE,
    sale_price DECIMAL(10, 3),
    cost_price DECIMAL(10, 3),
    profit DECIMAL(10, 3),
    FOREIGN KEY (item_id) REFERENCES Item(id)
);

CREATE TABLE Comment (
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id INT,
    item_id INT,
    comment_text TEXT,
    comment_date DATETIME,
    FOREIGN KEY (user_id) REFERENCES User(id),
    FOREIGN KEY (item_id) REFERENCES Item(id)
);

